const pythonShell = require('python-shell');

/**
 * Run md5crypt script, after complete call callback function(result)
 * @param password
 * @param callback
 */
module.exports = function(password, callback) {
    var options = {
        args: [password]
    };

    pythonShell.run('./md5crypt.py', options, function(err, results) {
        if (results.length === undefined) {
            throw new Error(err);
        }

        callback(results.shift());
    });
};